def gijswijt(n_terms):
 seq=[1]
 for m in range(1,n_terms):
  max_k=1
  for L in range(1,m+1):
   block=seq[-L:]
   k=0
   while L*(k+1)<=m and seq[-L*(k+1):]==block*(k+1):k+=1
   if k>max_k:max_k=k
  seq.append(max_k)
 return seq
