def pascal_triangle(rows):
 triangle=[[1]]
 for i in range(1,rows):
  prev=triangle[-1]
  row=[1]+[prev[j]+prev[j+1]for j in range(len(prev)-1)]+[1]
  triangle.append(row)
 return triangle
