 out=[list(row)for row in board]
 for y in range(n):
  for x in range(n):
   if board[y][x]!='.':continue
   for dy,dx in DIRS:
    ny,nx=y+dy,x+dx
    cnt=0
    while 0<=ny<n and 0<=nx<n and board[ny][nx]=='X':
     cnt+=1
     ny+=dy;nx+=dx
    if cnt>0 and 0<=ny<n and 0<=nx<n and board[ny][nx]=='O':
     out[y][x]='!'
     break
 return["".join(row)for row in out]
